INSERT INTO "Piece_of_Art" VALUES (9027,'Άγιος Πέτρος',3,0,'1600-1607','2020-01-01','2020-01-01','Λάδι σε μουσαμά','Λάδι',68.5,53.0,NULL,0,'Ο «Άγιος Πέτρος» ανήκει σε μια σειρά από προσωπογραφίες των δώδεκα Αποστόλων και του Χριστού, που δημιουργήθηκαν για πρώτη φορά από τον Ελ Γκρέκο και ονομάστηκαν «αποστολάτα». Στόλιζαν συνήθως το σκευοφυλάκιο των δυτικών καθολικών ναών, όπου οι ιερείς φύλασσαν τα ιερά σκεύη της εκκλησίας.Ο «Άγιος Πέτρος» είναι μια αυστηρή ρεαλιστική μορφή που κοιτάζει στοχαστικά πέρα από το θεατή. Κρατάει τα κλειδιά που του εμπιστεύθηκε ο Χριστός. Ο μπλε χιτώνας και το πορτοκαλί ιμάτιο έχουν ζωγραφιστεί με πλατειές πινελιές, ενώ λευκά φώτα σαν αστραπές τρέχουν πάνω στις πτυχές, θυμίζοντας τα φώτα και τις χρυσοκοντιλιές της βυζαντινής τέχνης.',NULL,NULL,NULL,NULL,NULL);
